import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: "Dima",
          id: 1,
        },
        {
          name: "Misha",
          id: 2,
        },
        {
          name: "Danya",
          id: 3,
        },
      ],
    };
  }

  render() {
    return (
      <div className='App'>
        {this.state.monsters.map((monster) => (
          <h1 key={monster.key}> {monster.name} </h1>
        ))}
      </div>
    );
  }
}

export default App;
