import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: "Dima",
        },
        {
          name: "Misha",
        },
        {
          name: "Danya",
        },
      ],
    };
  }

  render() {
    return (
      <div className='App'>
        {this.monsters.map((monster) => (
          <h1> {this.monsters.name} </h1>
        ))}
      </div>
    );
  }
}

export default App;
